var activeCls="active";$(function(){var $myContent=$(".legalcourt-content"),$titles=$myContent.find(".title");$titles.on("click",function(e){var $this=$(this),re=/arrow([rb])\.jpg$/,$img=$this.find("img"),$overview=$this.next(".overview"),active=$overview.hasClass(activeCls);active?($overview.removeClass(activeCls),$img.attr("src",function(index,val){return val.replace(re,"arrowr.jpg")})):($overview.addClass(activeCls),$img.attr("src",function(index,val){return val.replace(re,"arrowb.jpg")}))})});